@import url("https://fonts.googleapis.com/css2?family=Montserrat:ital,wght@0,100;0,200;0,300;0,900;1,300&family=Orbitron&family=Poppins:wght@100;400;500;600;700;800&display=swap");
* {
  margin: 0;
  box-sizing: border-box;
}
*:after, *:before {
  box-sizing: border-box;
}

html, body {
  overflow-x: hidden;
}

body {
  line-height: 1.5;
  font-family: "Poppins", sans-serif;
  -webkit-font-smoothing: antialiased;
}

button, input, textarea, select {
  font: inherit;
}

p, h1, h2, h3, h4, h5, h6 {
  overflow-wrap: break-word;
}

img, video, canvas, svg {
  max-width: 100%;
}

a {
  text-decoration: none;
  color: inherit;
}

ul {
  list-style: none;
}

.coolLink {
  position: relative;
  display: grid;
  place-items: center;
  overflow: hidden;
}
.coolLink:before {
  content: "";
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 2px;
  background-color: currentColor;
  transform: scale3d(0, 1, 1);
  transition: transform 0.4s ease-in-out;
  transform-origin: right;
}
.coolLink:after {
  content: attr(data-text);
  position: absolute;
  top: 0;
  height: 100%;
  transform: translate3d(150%, 0, 0);
  transition: transform 0.4s ease-in-out;
}
.coolLink span {
  transition: transform 0.3s cubic-bezier(0.5, 0.5, 0.3, 1);
}
.coolLink:hover:before {
  transform: scale3d(1, 1, 1);
  transform-origin: left;
}
.coolLink:hover:after {
  transform: translate3d(0, 0, 0);
}
.coolLink:hover span {
  transform: translate3d(-150%, 0, 0);
  transition: transform 0.3s cubic-bezier(0.5, 0.5, 0.3, 1);
}

.coolButton {
  position: relative;
  border: none;
  cursor: pointer;
  width: 175px;
  height: 120px;
  background: none;
  color: white;
}
.coolButton:before, .coolButton:after {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
}
.coolButton:before {
  content: "";
  background: #fff;
  clip-path: path("M154.5,88.5 C131,113.5 62.5,110 30,89.5 C-2.5,69 -3.5,42 4.5,25.5 C12.5,9 33.5,-6 85,3.5 C136.5,13 178,63.5 154.5,88.5 Z");
  transition: clip-path 0.5s cubic-bezier(0.585, 2.5, 0.645, 0.55);
}
.coolButton:after {
  content: "";
  height: 85%;
  width: 95%;
  top: 6px;
  border: 1px solid #fff;
  border-radius: 58% 42% 55% 45%/56% 45% 55% 44%;
  transform: rotate(-20deg);
  transition: transform 0.5s cubic-bezier(0.585, 2.5, 0.645, 0.55);
}
.coolButton:hover:after {
  transform: translate3d(0, -5px, 0);
}
.coolButton:hover:before {
  clip-path: path("M143,77 C117,96 74,100.5 45.5,91.5 C17,82.5 -10.5,57 5.5,31.5 C21.5,6 79,-5.5 130.5,4 C182,13.5 169,58 143,77 Z");
}
.coolButton span {
  mix-blend-mode: difference;
}
@media (max-width: 800px) {
  .coolButton {
    transform: scale3d(0.7, 0.7, 0.7);
    font-size: 1.3rem;
  }
}

.coloredIcon {
  color: #fff;
  font-size: 2rem;
}
.coloredIcon:hover {
  animation: coloredIconAnimation 0.2s infinite;
}
@keyframes coloredIconAnimation {
  0% {
    text-shadow: 5px 4px #f44336, -5px -6px #2196f3;
  }
  25% {
    text-shadow: -5px -6px #f44336, 5px 4px #2196f3;
  }
  50% {
    text-shadow: 5px -4px #f44336, -8px 4px #2196f3;
  }
  75% {
    text-shadow: -8px -4px #f44336, -5px -4px #2196f3;
  }
  100% {
    text-shadow: -5px 0px #f44336, 5px -4px #2196f3;
  }
}

